    private void btnCreateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCreateActionPerformed
        String full_name = txtFullname.getText();
        String username = txtUsername.getText();
        String password = new String(txtPassword.getPassword());
        String security_question = cmbSecurityquestion.getSelectedItem().toString();
        String security_answer = txtSecurityanswer.getText();

        UserModel newUser = new UserModel();
        newUser.setFull_name(full_name);
        newUser.setUsername(username);
        newUser.setPassword(password);
        newUser.setSecurity_question(security_question);
        newUser.setSecurity_answer(security_answer);

        if (Stream.of(
                newUser.getFull_name(),
                newUser.getUsername(),
                newUser.getPassword(),
                newUser.getSecurity_question(),
                newUser.getSecurity_answer()
        ).anyMatch(String::isEmpty)) {
            JOptionPane.showMessageDialog(this, "Please fill in all fields.");
            return;
        }
        try {
            UserDAO dao = new UserDAO(conn);
            boolean success = dao.signup(newUser);
            
            if (success) {
                JOptionPane.showMessageDialog(this, "User created successfully!");
                this.setVisible(false);
                new Login(conn).setVisible(true);  
            } else {
                JOptionPane.showMessageDialog(this, "Failed to create user.");
            } 
        } catch (SQLException e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }        

    }//GEN-LAST:event_btnCreateActionPerformed


